  `epDescription` varchar(255) DEFAULT NULL COMMENT '详情',
  PRIMARY KEY (`epId`)
) ENGINE=InnoDB AUTO_INCREMENT=7900 DEFAULT CHARSET=utf8 COMMENT='商品表';

